mapper.select:
SELECT distinct aj.`id` FROM `autoexec_job` aj
LEFT JOIN `autoexec_combop` ac ON aj.`operation_id` = ac.`id`
order by aj.`id` desc limit #{startNum}, #{pageSize}

SELECT aj.`id`, aj.`name`, aj.`status`, aj.`error_msg` as errorMsg, aj.`plan_start_time` as planStartTime, aj.`start_time` as startTime, aj.`end_time` as endTime, aj.`operation_id` as operationId, aj.`operation_type` as operationType, aj.`exec_user` as execUser, aj.`exec_user_type` as execUserType, aj.`source`, aj.`thread_count` as threadCount FROM `autoexec_job` aj WHERE aj.`id` in
#{jobId}
order by aj.`id` desc

SELECT `id`, `job_id` as jobId, `status`, `start_time` as startTime, `end_time` as endTime, `exec_user` as execUser, `exec_mode` as execMode, `name` as name, `uk` as uk FROM `autoexec_job_phase` where `job_id` = #{jobId}
and `status` in
#{status}

select count(1)
from `autoexec_job`
where `id` = #{jobId}
and `exec_user` in

    #{user}

SELECT COUNT(1)
FROM `autoexec_job` aj
WHERE aj.id != #{jobId}
  AND aj.`param_hash` = #{hash}

SELECT COUNT(1)
FROM `autoexec_job_phase_operation`
WHERE job_id != #{jobId}
  AND `param_hash` = #{hash}

SELECT count(ajn.`id`)
FROM `autoexec_job_node` ajn
where ajn.job_id = #{jobId}

select count(1)
from `autoexec_job_phase_node`
where `job_id` = #{jobId}
  and `job_phase_id` = #{jobPhaseId}
  and `host` = #{host}
  and port = #{port}

SELECT id,
       job_id as jobId,
       `status`
FROM `autoexec_job_phase`
WHERE `job_id` = #{jobId}
  and `name` = #{jobPhaseName}

select count(1)
from `autoexec_job_phase`
where `job_id` = #{jobId}
  and `sort` = #{sort}
  and `status` != 'completed'

SELECT sort
FROM `autoexec_job_phase`
WHERE job_id = #{jobId}
  AND `sort` > #{sort}
ORDER BY `sort` ASC
LIMIT 1

select `job_id` as jobId, `name`, `exec_mode` as execMode, `id`, `status`
from `autoexec_job_phase`
where `job_id` = #{jobId}
  and `sort` = #{sort}

SELECT COUNT(1) FROM `autoexec_job_phase_runner` WHERE `job_phase_id` in
#{jobPhaseId}
and `status` != #{status}

SELECT COUNT(1) FROM `autoexec_job_phase_runner` WHERE `job_phase_id` in
#{jobPhaseId}
and `status` = #{status}

SELECT ar.`id`, ar.`name`, ar.`url`, ar.`host`, ar.`port`
FROM `autoexec_runner` ar
WHERE ar.`id` = #{value}

mapper.update:
UPDATE `autoexec_job_phase`
SET `status` = #{status}

    ,`start_time` = NOW(3)


    ,`error_msg` = #{errorMsg}


    ,`end_time` = NOW(3)

WHERE id = #{id}

UPDATE `autoexec_job`
SET `status` = #{status}

    ,`start_time` = NOW(3)
    ,`end_time` = NULL


    ,`error_msg` = #{errorMsg}


    ,`end_time` = NOW(3)

WHERE id = #{id}

UPDATE `autoexec_job_phase`
SET `status` = #{status}

    ,`start_time` = NOW(3)


    ,`error_msg` = #{errorMsg}


    ,`end_time` = NOW(3)

WHERE id in

    #{phaseId}

UPDATE
`autoexec_job_phase_node`
SET

    `start_time` = NOW(3),


    `end_time` = NOW(3),

`status` = #{status}
WHERE `job_id` = #{jobId}
AND `job_phase_id` = #{jobPhaseId}
AND `id` = #{id}

UPDATE `autoexec_job_phase_node`
SET `status`     = #{status}
  , `start_time` = NULL

  , `end_time`   = NULL
WHERE `job_id` = #{jobId}
  and `status` != 'failed'
